grep -l "sample" ./dataset1/* | xargs -I {} sh -c 'if [ $(sort "{}" | uniq | grep -o "CSC510" {} | wc -l) -ge 3 ]; then echo "{}"; fi' | xargs -I {} sh -c 'echo "$(grep -o "CSC510" "{}" | wc -l) $(wc -c < "{}") {}"' | sort -k1,1nr -k2,2nr | gawk 'BEGIN {print "Count | Size | File\n------|------|---------------------------"} {printf "%5d | %4d | %s\n", $1, $2, $3}' | sed 's/file/filtered/g'
